import styled from "styled-components";
const Card = styled.div`
  width: 400px;
  height: 450px;
  box-sizing: border-box;
  cursor: pointer;
  background: linear-gradient(180deg, #fff 30%, #00652e70);
  box-shadow: 5px 5px 15px rgb(0 0 0 / 40%);
  border-radius: 10px;
`;
const CardHeader = styled.div`
  display: flex;
  justify-content: center;
`;
const CardFooter = styled.div`
  display: flex;
  justify-content: center;
  flex-flow: column;
  align-items: center;
  h2 {
    font-weight: 700;
    font-size: 28px;
    margin: 5px 0 0 0;
  }
  h3 {
    font-weight: 500;
  }
`;
const Img = styled.img`
  height: 300px;
  width: 300px;
`;
const Cards = ({ nome, ano, imagem, valor }) => {
  return (
    <>
      <div className={style.container__card}>
        <div className={style.card__header}>
          <img src={imagem} alt={nome} />
        </div >
        <CardFooter>
          <h2>{nome}</h2>
          <h3>{ano}</h3>
          <h3>{valor}</h3>
        </CardFooter>
      </div >
    </>
  );
};
export default Cards;
