import style from "./aside.module.scss";

const Aside = ({ marca, filtrarVeiculos, veiculos }) => {
  return (
    <div className={style.container}>
      <label>
        <input
          className={style.checkbox}
          type="checkbox"
          onClick={(e) => {
            if (e.target.checked === true) {
              filtrarVeiculos(marca);
            }else{
                veiculos={veiculos}
            }
          }}
        />
        {marca}
      </label>
    </div>
  );
};
export default Aside;
