import { Buttons } from "components/formFields";
import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";

export default function QRCodeModal({ onChange, isShowQRCode }) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Buttons
        variant="outline-dark"
        type={"button"}
        onClick={handleShow}
        name={"Gerar QR Code"}
        onChange={onChange}
      />

      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>{isShowQRCode && (<QRCode value />)}</Modal.Body>
        <Modal.Footer>
          <Buttons variant="secondary" onClick={handleClose}>
            Close
          </Buttons>
          <Buttons variant="primary" onClick={handleClose}>
            Save Changes
          </Buttons>
        </Modal.Footer>
      </Modal>
    </>
  );
}
