import React, { useState } from "react";
import { Buttons } from "components/formFields";
import Modal from "react-bootstrap/Modal";
import QRCode from "qrcode.react";


export default function QRCodeModal() {

  const [show, setShow] = useState(false);
  const [isShowQRCode, setShowQRCode] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  function handleQRCodeGenerator(){
      setShowQRCode(true);
  }

  const handleClick = () => {
    this.handleShow();
    // this.handleQRCodeGenerator();
  }
  return (
    <>
      <Buttons
        variant="outline-dark"
        type={"button"}
        onClick={handleClick}
        name={"Gerar QR Code"}
      />

      <Modal show={show} onHide={handleClose} onClick={handleQRCodeGenerator} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Resumo da venda</Modal.Title>
        </Modal.Header>
        <Modal.Body>{isShowQRCode && (<QRCode value />)}</Modal.Body>
      </Modal>
    </>
  );
}
