import React, { useState } from 'react';
import style from "./aside.module.scss";
import veiculos from "../home/veiculos.json"

const Aside = ({ marca, filtrarVeiculos, setVeiculos }) => {
    const [isDadosFiltrados, setDadosFiltrados] = useState([]) 
  return (
    <div className={style.container}>
      <label>
        <input
          className={style.checkbox}
          type="checkbox"
          onClick={(e) => {
            if (e.target.checked === true) {
              const resultado = filtrarVeiculos(marca);

              setDadosFiltrados([
                ...isDadosFiltrados,
                resultado
              ])
              console.log(isDadosFiltrados)
            } else {
                console.log(veiculos)
              return setVeiculos(veiculos);
            }
          }}
        />
        {marca}
      </label>
    </div>
  );
};
export default Aside;
